#!/bin/bashservice mariadb restart# Create databases and tablesmysql -u root <<EOFCREATE USER 'gth'@'localhost' IDENTIFIED BY '12345';GRANT ALL PRIVILEGES ON *.* TO 'gth'@localhost IDENTIFIED BY '12345';CREATE DATABASE IF NOT EXISTS sign_up;USE sign_up;CREATE TABLE IF NOT EXISTS register (    id INT NOT NULL AUTO_INCREMENT PRIMARY KEY,    firstName VARCHAR(50) NOT NULL,    lastName VARCHAR(50) NOT NULL,    phoneNo BIGINT(10) NOT NULL,    email VARCHAR(50) NOT NULL,    password VARCHAR(16) NOT NULL);CREATE DATABASE IF NOT EXISTS order_details;USE order_details;CREATE TABLE IF NOT EXISTS order_details (    id INT NOT NULL AUTO_INCREMENT PRIMARY KEY,    product VARCHAR(100) NOT NULL,    name VARCHAR(50) NOT NULL,    mobileNo BIGINT(10) NOT NULL,    email VARCHAR(50) NOT NULL,    pin BIGINT(10) NOT NULL,    address VARCHAR(200) NOT NULL,    image VARCHAR(150) NOT NULL);EOFapache2ctl -D FOREGROUND